INSERT INTO `app_exam_choice` VALUES('886','2','ລົດໃໝ່ຊື້ອອກຮ້ານ ບໍ່ທັນມີປ້າຍ','ລົດໃໝ່ຊື້ອອກຮ້ານ ບໍ່ທັນມີປ້າຍ','','1','250','6');
INSERT INTO `app_exam_choice` VALUES('887','3','ລົດເກົ່າທີ່ບໍ່ໄດ້ຜ່ານການກວດກາສະພາບເຕັກນິກຕາມກໍານົດເວລາ','ລົດເກົ່າທີ່ບໍ່ໄດ້ຜ່ານການກວດກາສະພາບເຕັກນິກຕາມກໍານົດເວລາ','','1','250','6');
