INSERT INTO `app_exam_choice` VALUES('897','1','ລົດເປ່ເພໜັກຈົນໃຊ້ການບໍ່ໄດ້','ລົດເປ່ເພໜັກຈົນໃຊ້ການບໍ່ໄດ້','','1','253','6');
INSERT INTO `app_exam_choice` VALUES('898','2','ສູນເສຍບຸຄະລາກອນ ຫຼື ບຸກຄົນສໍາຄັນຂອງຊາດ','ສູນເສຍບຸຄະລາກອນ ຫຼື ບຸກຄົນສໍາຄັນຂອງຊາດ','','1','253','6');
INSERT INTO `app_exam_choice` VALUES('899','3','ເສຍອະໄວຍະວະໃດໜຶ່ງຂອງຮ່າງກາຍທີ່ຈໍາເປັນຕ້ອງຕັດຖິ້ມ','ເສຍອະໄວຍະວະໃດໜຶ່ງຂອງຮ່າງກາຍທີ່ຈໍາເປັນຕ້ອງຕັດຖິ້ມ','','1','253','6');
INSERT INTO `app_exam_choice` VALUES('900','4','ຖືກທັງຂໍ້ ກ ແລະ ຂໍ້ ຂ','ຖືກທັງຂໍ້ ກ ແລະ ຂໍ້ ຂ','','1','253','6');
INSERT INTO `app_exam_choice` VALUES('901','1','ຮຽນຮູ້ເຕັກນິກ ແລະ ມາລະຍາດ ທີ່ດີ ໃນການຂັບຂີ່','ຮຽນຮູ້ເຕັກນິກ ແລະ ມາລະຍາດ ທີ່ດີ ໃນການຂັບຂີ່','','1','254','6');
INSERT INTO `app_exam_choice` VALUES('902','2','ບໍ່ຂັບລົດໃນຍາມມືນເມົາ','ບໍ່ຂັບລົດໃນຍາມມືນເມົາ','','1','254','6');
INSERT INTO `app_exam_choice` VALUES('903','3','ບໍ່ແລ່ນລົດໄວເກີນກໍານົດ','ບໍ່ແລ່ນລົດໄວເກີນກໍານົດ','','1','254','6');
INSERT INTO `app_exam_choice` VALUES('904','4','ຖືກໝົດທຸກຂໍ້','ຖືກໝົດທຸກຂໍ້','','1','254','6');
INSERT INTO `app_exam_choice` VALUES('905','1','ໃຈເຢັນ ຄ່ອຍເປັນຄ່ອຍໄປ ບໍ່ຮີບຮ້ອນ','ໃຈເຢັນ ຄ່ອຍເປັນຄ່ອຍໄປ ບໍ່ຮີບຮ້ອນ','','1','255','6');
INSERT INTO `app_exam_choice` VALUES('906','2','ໃຈຮ້ອນ, ມັກໃຊ້ອາລົມ ໃນເວລາຂັບຂີ່','ໃຈຮ້ອນ, ມັກໃຊ້ອາລົມ ໃນເວລາຂັບຂີ່','','1','255','6');
INSERT INTO `app_exam_choice` VALUES('907','3','ມັກສັງເກດ, ຫຼຽວຊ້າຍ ຫຼຽວຂວາ ເລື້ອຍໆ','ມັກສັງເກດ, ຫຼຽວຊ້າຍ ຫຼຽວຂວາ ເລື້ອຍໆ','','1','255','6');
INSERT INTO `app_exam_choice` VALUES('908','4','ຕັ້ງສະມາທິຢູ່ແຕ່ກັບການຂັບຂີ່ ບໍ່ສົນໃຈວ່າຜູ້ອື່ນຈະລົມກັນເລື້ອງຫຍັງ','ຕັ້ງສະມາທິຢູ່ແຕ່ກັບການຂັບຂີ່ ບໍ່ສົນໃຈວ່າຜູ້ອື່ນຈະລົມກັນເລື້ອງຫຍັງ','','1','255','6');
INSERT INTO `app_exam_choice` VALUES('909','1','ລົດຖີບຂອງລຸງສີຖືກລົດຢຽບຈົນມຸ່ນ','ລົດຖີບຂອງລຸງສີຖືກລົດຢຽບຈົນມຸ່ນ','','1','256','6');
INSERT INTO `app_exam_choice` VALUES('910','2','ເຮືອນປະຊາຊົນທີ່ຢູ່ລຽບຕາມແຄມທາງຖືກລົດຕໍາເສຍຫາຍທັງຫຼັງ','ເຮືອນປະຊາຊົນທີ່ຢູ່ລຽບຕາມແຄມທາງຖືກລົດຕໍາເສຍຫາຍທັງຫຼັງ','','1','256','6');
INSERT INTO `app_exam_choice` VALUES('911','3','ເສົາໄຟສັນຍານຈະລາຈອນ ຫຼືເສົາປ້າຍຈະລາຈອນຖືກລົດຕໍາຈົນຫັກ','ເສົາໄຟສັນຍານຈະລາຈອນ ຫຼືເສົາປ້າຍຈະລາຈອນຖືກລົດຕໍາຈົນຫັກ','','1','256','6');
INSERT INTO `app_exam_choice` VALUES('912','4','ຕົ້ນໝາກມ່ວງຢູ່ສວນແຄມທາງຖືກລົດປີ້ນທັບຈົນຫັກ','ຕົ້ນໝາກມ່ວງຢູ່ສວນແຄມທາງຖືກລົດປີ້ນທັບຈົນຫັກ','','1','256','6');
INSERT INTO `app_exam_choice` VALUES('913','1','ເມື່ອຝົນຕົກ ທາງມື່ນ ຜ່ອນຄວາມໄວລົງ ແລ່ນຊ້າໆ','ເມື່ອຝົນຕົກ ທາງມື່ນ ຜ່ອນຄວາມໄວລົງ ແລ່ນຊ້າໆ','','1','257','6');
INSERT INTO `app_exam_choice` VALUES('914','2','ຜ່ອນຄວາມໄວລົງດ້ວຍການໃສ່ເກຍຕໍ່າລົງຕາມລໍາດັບເມື່ອຮູ້ວ່າລະບົບເບກຂັດຂ້ອງ ເບກບໍ່ຢູ່','ຜ່ອນຄວາມໄວລົງດ້ວຍການໃສ່ເກຍຕໍ່າລົງຕາມລໍາດັບເມື່ອຮູ້ວ່າລະບົບເບກຂັດຂ້ອງ ເບກບໍ່ຢູ່','','1','257','6');
INSERT INTO `app_exam_choice` VALUES('915','3','ໃສ່ເກຍຕໍ່າ ເມື່ອຂັບລົດລົງຄ້ອຍສູງຊັນ','ໃສ່ເກຍຕໍ່າ ເມື່ອຂັບລົດລົງຄ້ອຍສູງຊັນ','','1','257','6');
INSERT INTO `app_exam_choice` VALUES('916','4','ຢຽບເບກ ເພື່ອຢຸດລົດທັນທີ ເມື່ອຮູ້ວ່າ ຕີນລົດແຕກ','ຢຽບເບກ ເພື່ອຢຸດລົດທັນທີ ເມື່ອຮູ້ວ່າ ຕີນລົດແຕກ','','1','257','6');
INSERT INTO `app_exam_choice` VALUES('917','1','ການຊ່ວຍເຫຼືອເບື້ອງຕົ້ນແກ່ຜູ້ບາດເຈັບຢູ່ບ່ອນເກີດອຸປະຕິເຫດ ກ່ອນນໍາສົ່ງໂຮງໝໍ','ການຊ່ວຍເຫຼືອເບື້ອງຕົ້ນແກ່ຜູ້ບາດເຈັບຢູ່ບ່ອນເກີດອຸປະຕິເຫດ ກ່ອນນໍາສົ່ງໂຮງໝໍ','','1','258','7');
INSERT INTO `app_exam_choice` VALUES('918','2','ການປິ່ນປົວຄົນເຈັບເບື້ອງຕົ້ນ ຢູ່ຫ້ອງສຸກເສີນ ກ່ອນນໍາເຂົ້າຜ່າຕັດ','ການປິ່ນປົວຄົນເຈັບເບື້ອງຕົ້ນ ຢູ່ຫ້ອງສຸກເສີນ ກ່ອນນໍາເຂົ້າຜ່າຕັດ','','1','258','7');
INSERT INTO `app_exam_choice` VALUES('919','3','ການຊ່ວຍຜູ້ບາດເຈັບ ອອກຈາກບ່ອນເກີດອຸປະຕິເຫດ','ການຊ່ວຍຜູ້ບາດເຈັບ ອອກຈາກບ່ອນເກີດອຸປະຕິເຫດ','','1','258','7');
INSERT INTO `app_exam_choice` VALUES('920','4','ການຊ່ວຍເອົາຜູ້ບາດເຈັບໄປສົ່ງໂຮງໝໍ','ການຊ່ວຍເອົາຜູ້ບາດເຈັບໄປສົ່ງໂຮງໝໍ','','1','258','7');
INSERT INTO `app_exam_choice` VALUES('921','1','ເພື່ອຊ່ວຍໃຫ້ຜູ້ບາດເຈັບ ຫາຍຈາກອາການບາດເຈັບໂດຍໄວ','ເພື່ອຊ່ວຍໃຫ້ຜູ້ບາດເຈັບ ຫາຍຈາກອາການບາດເຈັບໂດຍໄວ','','1','259','7');
INSERT INTO `app_exam_choice` VALUES('922','2','ເພື່ອຮັກສາອາການຂອງຜູ້ບາດເຈັບບໍ່ໃຫ້ຊຸດໂຊມລົງ ຊຶ່ງອາດຈະພາໃຫ້ພິການຫຼືເສຍຊີວິດໄດ້','ເພື່ອຮັກສາອາການຂອງຜູ້ບາດເຈັບບໍ່ໃຫ້ຊຸດໂຊມລົງ ຊຶ່ງອາດຈະພາໃຫ້ພິການຫຼືເສຍຊີວິດໄດ້','','1','259','7');
INSERT INTO `app_exam_choice` VALUES('923','3','ເພືອຊ່ວຍໃຫ້ອາການບາດເຈັບຫາຍດີເປັນປົກກະຕິ','ເພືອຊ່ວຍໃຫ້ອາການບາດເຈັບຫາຍດີເປັນປົກກະຕິ','','1','259','7');
INSERT INTO `app_exam_choice` VALUES('924','4','ຖືກໝົດທຸກຂໍ້','ຖືກໝົດທຸກຂໍ້','','1','259','7');
INSERT INTO `app_exam_choice` VALUES('925','1','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນນິ້ງ ບໍ່ເໜັງຕີງ','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນນິ້ງ ບໍ່ເໜັງຕີງ','','1','260','7');
INSERT INTO `app_exam_choice` VALUES('926','2','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນດິ້ນໄປມາ','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນດິ້ນໄປມາ','','1','260','7');
INSERT INTO `app_exam_choice` VALUES('927','3','ເມື່ອສັງເກດເຫັນວ່າຜູ້ບາດເຈັບບໍ່ຫາຍໃຈ','ເມື່ອສັງເກດເຫັນວ່າຜູ້ບາດເຈັບບໍ່ຫາຍໃຈ','','1','260','7');
INSERT INTO `app_exam_choice` VALUES('928','4','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນໝົດສະຕິຢູ່','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນໝົດສະຕິຢູ່','','1','260','7');
INSERT INTO `app_exam_choice` VALUES('929','1','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນນິ້ງ ບໍ່ເໜັງຕີງ','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນນິ້ງ ບໍ່ເໜັງຕີງ','','1','261','7');
INSERT INTO `app_exam_choice` VALUES('930','2','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນໝົດສະຕິຢູ່','ເມື່ອສັງເກດເຫັນຜູ້ບາດເຈັບນອນໝົດສະຕິຢູ່','','1','261','7');
INSERT INTO `app_exam_choice` VALUES('931','3','ເມື່ອສັງເກດເຫັນມີເລືອດໄຫຼອອກຈາກປາກ ຫຼື ດັງ','ເມື່ອສັງເກດເຫັນມີເລືອດໄຫຼອອກຈາກປາກ ຫຼື ດັງ','','1','261','7');
