INSERT INTO `app_exam_choice` VALUES('1972','2','ຮູ້ຈັກໃຊ້ເລ່ຫລ່ຽມ ເພື່ອເອົາໂຕລອດໃນຍາມຈໍາເປັນ','ຮູ້ຈັກໃຊ້ເລ່ຫລ່ຽມ ເພື່ອເອົາໂຕລອດໃນຍາມຈໍາເປັນ','','1','567','16');
