INSERT INTO `app_exam_choice` VALUES('1990','4','ການເວົ້າຈາບໍ່ຖຶກຕ້ອງຕາມກາລະເທສະ','ການເວົ້າຈາບໍ່ຖຶກຕ້ອງຕາມກາລະເທສະ','','1','571','16');
