INSERT INTO `app_exam_question` VALUES('115','ລົດສອງຄັນນີ້ ຄັນໃດລະເມີດປ້າຍກໍານົດ?','ລົດສອງຄັນນີ້ ຄັນໃດລະເມີດປ້າຍກໍານົດ?','static/upload/2024/6/ques_img/cat_2/41.png','3','1','2');
