INSERT INTO `app_exam_question` VALUES('460','ລະບົບຮອງຮັບນ້ຳໜັກມີໜ້າທີ່ຫຍັງ ?','ລະບົບຮອງຮັບນ້ຳໜັກມີໜ້າທີ່ຫຍັງ ?','','1','1','13');
