INSERT INTO `app_exam_question` VALUES('693','ຫາງລາກຕ້ອງຂຶ້ນທະບຽນບໍ່ ?','ຫາງລາກຕ້ອງຂຶ້ນທະບຽນບໍ່ ?','','3','1','20');
